        keys.forEach(function (key) {
            if (currRef.hasOwnProperty(key)) {
                currRef = currRef[key];
            } else {
                RG.err('Factory.World', 'getConf', 'Cannot find conf for keys ' + JSON.stringify(keys));
            }
        });
